-- Product: VÒNG TRẦM HƯƠNG 108 HẠT 6mm CHÚ ĐẠI BI HÌNH LU THỐNG - 50 NĂM TUỔI
INSERT INTO products (
  haravan_product_id, name_vi, slug, description_vi, excerpt_vi,
  seo_title_vi, seo_description_vi, brand, product_type, tags, status,
  haravan_created_at, haravan_updated_at
) VALUES (
  '1043833940',
  'VÒNG TRẦM HƯƠNG 108 HẠT 6mm CHÚ ĐẠI BI HÌNH LU THỐNG - 50 NĂM TUỔI',
  'vong-tram-huong-108-hat-chu-dai-bi-hinh-lu-thong-50-nam-tuoi',
  '<p style="text-align: center;"><strong><span style="color:#f39c12">VÒNG TRẦM HƯƠNG 108 HẠT CHÚ ĐẠI BI HÌNH LU THỐNG - 50 NĂM TUỔI</span></strong></p><p><strong>THÔNG TIN CHI TIẾT</strong></p><div><table border="1" cellspacing="1" cellpadding="1" style="width:658px"><tbody><tr><td style="width:180px">SỐ HẠT</td><td style="width:464px">108 hạt trầm tự nhiên cao cấp</td></tr><tr><td style="width:180px">KÍCH THƯỚC</td><td style="width:464px">6mm</td></tr><tr><td style="width:180px">LOẠI TRẦM</td><td style="width:464px">Trầm hương rừng tự nhiên 50 năm tuổi</td></tr><tr><td style="width:180px">LOẠI CHARM</td><td style="width:464px">Charm ngọc khắc Chú Đại Bi hình Lu Thống</td></tr><tr><td style="width:180px">MÀU SẮC</td><td style="width:464px">Đỏ, Vàng, Đen</td></tr><tr><td style="width:180px">BỘ SẢN PHẨM GỒM</td><td style="width:464px">- Vòng Trầm hương 108 hạt mix ngọc Chú Đại Bi hình Lu Thống<br>- 01 Hộp gấm bảo quản vòng<br>- 01 Thẻ Bảo Hành</td></tr><tr><td style="width:180px">BẢO HÀNH</td><td style="width:464px">1 ĐỔI 1 trong vòng 1 THÁNG nếu có lỗi kỹ thuật</td></tr><tr><td style="width:180px">ĐEO VÒNG THEO MỆNH</td><td style="width:464px"><strong>- Mệnh Kim:&nbsp;</strong>Đeo<strong>&nbsp;</strong>charm ngọc<strong>&nbsp;</strong>màu<strong>&nbsp;</strong>trắng hoặc màu vàng<br><strong>- Mệnh Mộc:</strong>&nbsp;Đeo charm ngọc<strong>&nbsp;</strong>&nbsp;màu xanh hoặc màu đen<br><strong>- Mệnh Thuỷ:&nbsp;</strong>Đeo charm ngọc&nbsp;đen hoặc màu trắng<br><strong>- Mệnh Hoả:&nbsp;</strong>Đeo charm ngọc<strong>&nbsp;</strong>&nbsp;đỏ hoặc màu xanh<br><strong>- Mệnh Thổ:&nbsp;</strong>Đeo&nbsp;charm ngọc<strong>&nbsp;</strong>vàng hoặc đỏ</td></tr></tbody></table><p>&nbsp;</p></div><h2 style="text-align: justify;"><span style="color:#f39c12">MÔ TẢ SẢN PHẨM VÒNG TRẦM HƯƠNG 108 HẠT CHÚ ĐẠI BI</span></h2><h2 style="text-align: justify;">&nbsp;</h2><p style="text-align: justify;">Vòng trầm hương 108 hạt Chú Đại Bi của Sơn Hồng là chiếc vòng tay mang màu sắc tâm linh sâu sắc. Vòng tay không chỉ là một món đồ trang sức thông thường mà trên charm ngọc mix trên chiếc vòng tay này là những dòng chữ mang công năng vi diệu của sự giải thoát, cứu khổ cứu nạn mà Quan Thế Âm Bồ Tát đã phát nguyện cứu độ chúng sanh. Vòng tay trầm hương Đại Bi chính là một lời nhắc nhở thâm thuý, mang đậm lòng từ bi đối với mỗi chúng ta trên con đường tu tập, giác ngộ Phật tánh.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng được chế tác từ gỗ Trầm hương tự nhiên Việt Nam 50 năm tuổi kết hợp cùng charm ngọc hình Lu Thống khắc Chú Đại Bi với giúp người đeo tăng cường năng lượng tích cực, thu hút tài lộc và bình an trong cuộc sống</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng tay Trầm hương mix ngọc Chú Đại Bi hình Lu Thống sở hữu mùi hương ngọt dịu, nhẹ nhàng giúp người đeo xua tan cảm giác mệt mỏi, u sầu, giải tỏa stress cũng như xoa dịu những nỗi buồn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Charm hình Lu Thống có ý nghĩa vô cùng tốt đối với người sở hữu và nó được mệnh danh là "Hanh Thông Vạn Sự". Charm mix với Trầm Hương sẽ giúp cho sự nghiệp thuật lợi, học hành thi cử đỗ đạt.&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12">Ý NGHĨA CỦA BIỂU TƯỢNG CỦA CHÚ ĐẠI BI</span></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Chú Đại Bi là câu thần chú được trích từ Kinh Thiên Thủ Thiên Nhãn Quán Thế Âm Bồ Tát Quảng Đại Viên Mãn Vô Ngại Đại Bi Tâm Đà Ra Ni. Và Chú Đại Bi là câu thần chú mang uy lực có tác động mạnh mẽ đến tất thảy chúng sanh trên con đường giải thoát.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Câu thần chú mong muốn chúng sanh được an vui, diệt trừ được tất thảy bệnh tật, sống lâu, được giàu có, được tiêu trừ toàn bộ nghiệp chướng của nhiều đời nhiều kiếp, được chứng quả thành tựu công đức, được thành tựu tất cả thiện căn, không lo sợ hãi, được mong cầu đầy đủ như ý.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12">CÔNG DỤNG CỦA VÒNG TRẦM HƯƠNG 108 HẠT CHÚ ĐẠI BI</span></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Chiếc vòng trầm hương 108 hạt Chú Đại Bi&nbsp;giúp người sở hữu khơi dậy lòng từ bi nơi trái tim, biết yêu thương, từ đó ươm mầm trí tuệ, bởi lòng từ bi chính là mảnh đất ươm trồng trí tuệ tốt nhất của ta. . Không chỉ vậy, khi đeo chúng còn mang lại sự thanh thản, trí tuệ về cảm xúc cho nơi làm việc và trong cuộc sống. Một chiếc vòng tay đơn giản với trầm hương và Chú Đại Bi sẽ là lời nhắc nhở giúp người đeo luôn tinh tấn tâm trí, khơi dậy lòng từ bi, hiểu được nhân quả, từ đó biết cách hành xử trong cuộc sống.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>VỀ TRẦM HƯƠNG VIỆT NAM</strong></p><p style="text-align: justify;">Gỗ trầm hương Việt Nam được giới chuyên gia về trầm đánh giá rất cao trên thế giới về mùi hương, cũng như chất lượng nhờ vào điều kiện khí hậu &amp; thổ nhưỡng đặc trưng của rừng rậm nhiệt đới, nơi mà cây trầm phát triển và tạo trầm tốt nhất. Trầm Hương Việt Nam thường sinh trưởng tại các khu rừng rậm nhiệt đới ở Quảng Nam, Tây Nguyên, Phú Quốc,.. nơi cho ra trầm tốt nhất thế giới.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>GIỚI THIỆU VỀ&nbsp; TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: justify;">Đối với bạn trầm hương là sự may mắn, đối với Sơn Hồng, trầm hương là tâm huyết của gia đình chúng tôi. Với hơn 30 năm làm trầm, chúng tôi hiểu giá trị của Trầm Hương và mong muốn mang trầm hương Việt đến với người Việt cùng với lời chúc về sự thịnh vượng và may mắn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>CHÚNG TÔI CAM KẾT</strong></p><p style="text-align: justify;">- Nguồn gốc đúng: Trầm Hương Việt Nam được giới chuyên về trầm đánh giá tốt nhất thế giới về mùi thơm cũng như chất lượng.</p><p style="text-align: justify;">- Đúng &amp; đủ tuổi trầm như đã công bố: Chúng tôi cam kết sử dụng đúng loại trầm có số tuổi như đã công bố trên từng sản phẩm.&nbsp;</p><p style="text-align: justify;">- 100% sử dụng nguyên liệu tự nhiên: Chúng tôi không sử dụng bất kỳ hoá chất tạo màu hoặc tạo mùi nào trong tất cả các sản phẩm về trầm hương của Sơn Hồng.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>QUY TRÌNH SẢN XUẤT VÒNG TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/qtct-5_b4fc5c3526894490bde30264cf1ee3ae_1024x1024.jpg"></p><p style="text-align: justify;"><br>&nbsp;</p>',
  NULL,
  'VÒNG TRẦM HƯƠNG 108 HẠT CHÚ ĐẠI BI HÌNH LU THỐNG - 50 NĂM TUỔI',
  'Vòng trầm hương 108 hạt Chú Đại Bi hình Lu Thống của Sơn Hồng là chiếc vòng tay mang màu sắc tâm linh sâu sắc. Vòng tay không chỉ là một món đồ trang sức thông thường mà trên charm ngọc mix trên chiếc vòng tay này là những dòng chữ mang công năng vi diệu của sự giải thoát, cứu khổ cứu nạn.',
  'TRẦM HƯƠNG SƠN HỒNG',
  'VÒNG TAY',
  ARRAY['NỮ', 'NAM', 'vòng 108 hạt', '50 năm', 'đá'],
  'published',
  '2022-12-18T00:30:04+07:00'::timestamp,
  '2024-08-12T10:39:08+07:00'::timestamp
) ON CONFLICT (haravan_product_id) DO NOTHING;

