-- Product: VÒNG TRẦM HƯƠNG TỐC CỎ 4 LÁ MAY MẮN
INSERT INTO products (
  haravan_product_id, name_vi, slug, description_vi, excerpt_vi,
  seo_title_vi, seo_description_vi, brand, product_type, tags, status,
  haravan_created_at, haravan_updated_at
) VALUES (
  '1043837275',
  'VÒNG TRẦM HƯƠNG TỐC CỎ 4 LÁ MAY MẮN',
  'vong-tram-huong-toc-co-4-la-may-man',
  '<p style="text-align: center;"><strong><span style="color:#f39c12">VÒNG TRẦM HƯƠNG TỐC CỎ 4 LÁ MAY MẮN</span></strong></p><p style="text-align: justify;"><strong>THÔNG TIN CHI TIẾT</strong></p><table border="1" cellspacing="1" cellpadding="1" style="width:641px"><tbody><tr><td style="width:147px">SỐ HẠT</td><td style="width:480px">21 hạt trầm hương tự nhiên</td></tr><tr><td style="width:147px">KÍCH THƯỚC</td><td style="width:480px">8mm</td></tr><tr><td style="width:147px">LOẠI TRẦM</td><td style="width:480px">Trầm hương rừng tự nhiên 50 năm tuổi</td></tr><tr><td style="width:147px">LOẠI ĐÁ</td><td style="width:480px">Ngọc bích và Aquamarine</td></tr><tr><td style="width:147px">MÀU SẮC</td><td style="width:480px">Xanh ngọc, Xanh lục, Cam đỏ</td></tr><tr><td style="width:147px">BỘ SẢN PHẨM GỒM</td><td style="width:480px">- Vòng Trầm hương tốc mix ngọc cỏ bốn lá<br>- 01 Hộp gấm bảo quản vòng<br>- 01 Thẻ Bảo Hành</td></tr><tr><td style="width:147px">BẢO HÀNH</td><td style="width:480px">1 ĐỔI 1 trong vòng 1 THÁNG nếu có lỗi kỹ thuật</td></tr></tbody></table><h2 style="text-align: justify;">&nbsp;</h2><h2 style="text-align: justify;"><strong><span style="color:#f39c12">MÔ TẢ SẢN PHẨM VÒNG TRẦM HƯƠNG CỎ 4 LÁ MAY MẮN</span></strong></h2><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/4ln_xanh_duong_1_8cab3776c28340a7a078ef0cf24f9d67_grande.jpg"></p><p style="text-align: justify;">Vòng Trầm hương Cỏ 4 Lá may mắn của Sơn Hồng là chiếc vòng tay được thiết kế theo thiên hướng nhẹ nhàng và giúp người đeo luôn gặp được những điều may mắn trong cuộc sống, tạo thêm niềm tin và hy vọng vào những điều tốt đẹp phía trước.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Chiếc vòng là sự kết hợp hoàn hảo của gỗ Trầm hương tự nhiên 50 năm tuổi cùng charm ngọc Cỏ 4 Lá may mắn. Các chất liệu ngọc gồm aquamarine và ngọc bích tự nhiên.&nbsp;Vòng tay thường được xâu chuỗi từ 17-21 hạt trầm. Số hạt sẽ được chọn dựa trên quy tắc của vòng luân hồi “sinh – lão – bệnh – tử” nhằm đem lại may mắn cho người đeo. Nên chọn số hạt là số chia cho 4 dư 1. Theo đó, hạt cuối cùng sẽ rơi vào chữ “sinh” - mang ý nghĩa may mắn và bình an.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng tay Trầm hương mix ngọc Cỏ 4 Lá sở hữu mùi hương ngọt dịu, nhẹ nhàng giúp người đeo xua tan cảm giác mệt mỏi, u sầu, giải tỏa stress cũng như xoa dịu những nỗi buồn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>Ý NGHĨA CỦA BIỂU TƯỢNG CỦA CỎ 4 LÁ</strong></span></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Cỏ 4 Lá là hình tượng đại diện cho sự may mắn vì việc bắt gặp hình ảnh một ngọn cỏ bốn lá là điều rất hy hữu, cứ 100.00 cây cỏ ba lá, người ta mới có thể tìm thấy một cây cỏ bốn lá. Vì vậy, người ta tin rằng những người nhìn thấy cỏ bốn lá sẽ gặp được rất nhiều may mắn. Bốn cánh của cỏ bốn lá tượng trưng cho sự trung thành, hy vọng, tình yêu thiêng liêng và sự May mắn. Chính vì thế, những ai có được cỏ bốn lá sẽ gặp được rất nhiều may mắn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>CÔNG DỤNG CỦA VÒNG TRẦM HƯƠNG CỎ 4 LÁ</strong></span></h2><p style="text-align: justify;">Vòng tay charm ngọc&nbsp;4 lá nhà Sơn Hồng&nbsp;mang đến sự may mắn, thành công, thịnh vượng, cho những người đeo nó. Bên cạnh đó, đây còn là chiếc bùa hộ mệnh của chủ nhân, giúp họ vượt qua nhiều sóng gió, khó khăn của cuộc sống.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>VỀ TRẦM HƯƠNG VIỆT NAM</strong></p><p style="text-align: justify;">Gỗ trầm hương Việt Nam được giới chuyên gia về trầm đánh giá rất cao trên thế giới về mùi hương, cũng như chất lượng nhờ vào điều kiện khí hậu &amp; thổ nhưỡng đặc trưng của rừng rậm nhiệt đới, nơi mà cây trầm phát triển và tạo trầm tốt nhất. Trầm Hương Việt Nam thường sinh trưởng tại các khu rừng rậm nhiệt đới ở Quảng Nam, Tây Nguyên, Phú Quốc,.. nơi cho ra trầm tốt nhất thế giới.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>GIỚI THIỆU VỀ&nbsp; TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: justify;">Đối với bạn trầm hương là sự may mắn, đối với Sơn Hồng, trầm hương là tâm huyết của gia đình chúng tôi. Với hơn 30 năm làm trầm, chúng tôi hiểu giá trị của Trầm Hương và mong muốn mang trầm hương Việt đến với người Việt cùng với lời chúc về sự thịnh vượng và may mắn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>CHÚNG TÔI CAM KẾT</strong></p><p style="text-align: justify;">- Nguồn gốc đúng: Trầm Hương Việt Nam được giới chuyên về trầm đánh giá tốt nhất thế giới về mùi thơm cũng như chất lượng.</p><p style="text-align: justify;">- Đúng &amp; đủ tuổi trầm như đã công bố: Chúng tôi cam kết sử dụng đúng loại trầm có số tuổi như đã công bố trên từng sản phẩm.&nbsp;</p><p style="text-align: justify;">- 100% sử dụng nguyên liệu tự nhiên: Chúng tôi không sử dụng bất kỳ hoá chất tạo màu hoặc tạo mùi nào trong tất cả các sản phẩm về trầm hương của Sơn Hồng.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>QUY TRÌNH SẢN XUẤT VÒNG TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/qtct-5_b4fc5c3526894490bde30264cf1ee3ae_1024x1024.jpg"></p><p style="text-align: justify;"><br>&nbsp;</p>',
  NULL,
  'VÒNG TRẦM HƯƠNG TỐC CỎ 4 LÁ MAY MẮN',
  'Vòng Trầm hương Cỏ 4 Lá may mắn của Sơn Hồng là chiếc vòng tay được thiết kế theo thiên hướng nhẹ nhàng và giúp người đeo luôn gặp được những điều may mắn trong cuộc sống, tạo thêm niềm tin và hy vọng vào những điều tốt đẹp phía trước.',
  'SƠN HỒNG',
  'VÒNG TAY',
  ARRAY['NỮ', '50 năm', 'TỐC ĐƠN', 'đá'],
  'published',
  '2022-12-18T13:41:24+07:00'::timestamp,
  '2022-12-27T18:20:11+07:00'::timestamp
) ON CONFLICT (haravan_product_id) DO NOTHING;

