-- Product: VÒNG TRẦM HƯƠNG TỐC NGỌC BẢO MỆNH - 50 NĂM TUỔI
INSERT INTO products (
  haravan_product_id, name_vi, slug, description_vi, excerpt_vi,
  seo_title_vi, seo_description_vi, brand, product_type, tags, status,
  haravan_created_at, haravan_updated_at
) VALUES (
  '1043851481',
  'VÒNG TRẦM HƯƠNG TỐC NGỌC BẢO MỆNH - 50 NĂM TUỔI',
  'vong-tram-huong-toc-ngoc-bao-menh-50-nam',
  '<p style="text-align: center;"><span style="font-size:14px"><span style="color:#f39c12"><strong>VÒNG TRẦM HƯƠNG TỐC NGỌC BẢO MỆNH&nbsp;</strong></span></span></p><p style="text-align: justify;"><strong>THÔNG TIN CHI TIẾT</strong></p><table border="1" cellspacing="1" cellpadding="1" style="width:638px"><tbody><tr><td style="width:177px">SỐ HẠT</td><td style="width:447px">21 hạt</td></tr><tr><td style="width:177px">KÍCH THƯỚC</td><td style="width:447px">8mm</td></tr><tr><td style="width:177px">LOẠI TRẦM</td><td style="width:447px">Trầm hương tự nhiên Việt Nam 50 năm tuổi</td></tr><tr><td style="width:177px">ĐẶC TÍNH</td><td style="width:447px">Mùi thơm ngọt dịu, nhẹ nhàng</td></tr><tr><td style="width:177px">LOẠI ĐÁ</td><td style="width:447px">Cẩm Thạch; Ngọc Bích; Cẩm Huyết,&nbsp;Sapphire;&nbsp;Aquamarine Greenish blue; Thạch Anh tóc</td></tr><tr><td style="width:177px">BỘ SẢN PHẨM GỒM</td><td style="width:447px">- Vòng Trầm hương tốc Ngọc Bảo Mệnh<br>- 01 Hộp gấm bảo quản vòng<br>- 01 Thẻ Bảo Hành</td></tr><tr><td style="width:177px">BẢO HÀNH</td><td style="width:447px">1 ĐỔI 1 trong vòng 1 THÁNG nếu có lỗi kỹ thuật</td></tr><tr><td style="width:177px">CÁCH BẢO QUẢN VÒNG</td><td style="width:447px">Nên để vòng vào lọ thuỷ tinh (tốt nhất là có sẵn một ít bột trầm để hút ẩm và giữ hương thơm bền). Sau khi đeo một thời gian dây sẽ giảm mùi thơm, khi bỏ lại vào hũ thuỷ tinh để dưỡng hương thì vòng sẽ thơm lại một cách tự nhiên</td></tr><tr><td style="width:177px">ĐEO VÒNG THEO MỆNH</td><td style="width:447px">- Vàng cẩm thạch&nbsp;- Phù hợp mệnh Thổ và mệnh Kim<br>- Xanh Lục Ngọc Bích - Phù hợp với mệnh Hoả và mệnh Mộc<br>- Cẩm thạch huyết - Phù hợp với mệnh Hoả và mệnh Thổ<br>- Đá Sapphire - Phù hợp mệnh Thuỷ và mệnh Mộc<br>- Thạch Anh hồng - Phù hợp mệnh Hoả và mệnh Thổ<br>- Cẩm thạch tím - Phù hợp với Mệnh Hoả và Thổ<br>- Thạch Anh đỏ cánh sen - Phù hợp mệnh Hoả và Thổ<br>- Thạch anh đen sợi tóc - Phù hợp mệnh Thuỷ và mệnh Mộc<br>- Aquamarine Greenish blue - Phù hợp mệnh Thuỷ và Mộc<br>- Thạch anh trắng - Phù hợp mệnh Kim và mệnh Thuỷ</td></tr></tbody></table><h2 style="text-align: justify;">&nbsp;</h2><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>MÔ TẢ VÒNG TRẦM HƯƠNG TỐC NGỌC BẢO MỆNH</strong></span></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Vòng Trầm hương Ngọc Bảo Mệnh được coi là “lá bùa” bảo mệnh hộ chủ và mang lại những điều may mắn cho người sở hữu.&nbsp;</strong></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Chiếc vòng là sự kết hợp hoàn hảo của 21 hạt&nbsp;Trầm hương tự nhiên 50 năm tuổi cùng các charm “đá bảo mệnh” theo ngũ hành: Kim - Mộc - Thuỷ- Hoả- Thổ. Vòng trầm Bảo Mệnh cao cấp Sơn Hồng giống như một vật để hộ thân với mong muốn cầu mong bình an và hy vọng xua đuổi những vận khí xấu.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng tay Trầm hương tốc Ngọc Bảo mệnh có mùi hương ngọt dịu, nhẹ nhàng giúp người đeo xua tan cảm giác mệt mỏi, u sầu và xoa dịu những nỗi buồn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><strong><span style="color:#f39c12">Ý NGHĨA TỪNG LOẠI NGỌC BẢO MỆNH THEO NGŨ HÀNH</span></strong></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Vàng cẩm thạch&nbsp;- Phù hợp mệnh Thổ và mệnh Kim</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_vang_1_9bbdfe9163f447eab1e41fdc894dfae3_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Màu vàng là màu thuộc ngũ hành Thổ. Theo quy luật tương sinh thì ngũ hành Thổ tương sinh ra ngũ hành Kim. Do đó, màu vàng hợp nhất mệnh Kim và sau đó là đến mệnh Thổ.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Hơn thế, màu vàng tượng trưng cho màu sắc ấm áp của ánh nắng mặt trời, ngụ ý cho hạnh phúc, dinh dưỡng. Màu vàng còn là màu của lửa, tượng trưng cho nguồn năng lượng sôi động, tích cực, giúp đem tới sự tốt lành, may mắn cho gia chủ.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Xanh Lục Ngọc Bích - Phù hợp với mệnh Hoả và mệnh Mộc</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_luc_1_78453f30edf044abb8e806895e3d2e00_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Xét theo quy luật ngũ hành thì màu xanh lá cây thuộc hành Mộc mà Thủy sinh Mộc nên màu xanh lá cây hợp với mệnh Thủy. Bên cạnh đó mệnh Mộc cũng có thể sử dụng màu xanh này.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Ngoài ra, Màu xanh lá cây (lục) theo phong thủy là màu đại diện của sự đổi mới, năng lượng mới và sự tái tạo. Đây được xem là màu rất có lợi cho sức khỏe vì nó có khả năng tạo trạng thái cân bằng cho toàn bộ cơ thể.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Cẩm thạch huyết - Phù hợp với mệnh Hoả và mệnh Thổ</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_do_3_6e02fef6a6b14618844732b658e9fb72_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng tay ngọc cẩm thạch huyết là một trong những món trang sức đại diện cho tầng lớp quý tộc thời xưa. Cẩm thạch huyết không chỉ thể hiện sự giàu sang, phú quý mà còn giúp người đeo toát lên&nbsp; khí chất cao quý, sang trọng và sang trọng.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Màu đỏ huyết của đá cẩm thạch này tuyệt nhiên là biểu tượng may mắn, thịnh vượng, tượng trưng cho vua&nbsp; chúa như một quyền lực tối cao, giúp đánh đuổi tà khí.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Đá Sapphire - Phù hợp mệnh Thuỷ và mệnh Mộc</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_xanh_bien_322bd934ac944241a59b2c901338f959_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Theo ngũ hành, đá Sapphire là bùa may mắn cho những người mệnh Mộc, mệnh Thủy. Theo đó, khi mang trang sức đính đá Sapphire, chủ nhân sẽ luôn gặp được may mắn và thịnh vượng, đại cát, đại lộc trong đời sống. Ngoài ra, đá Sapphire cũng như nhiều loại đá quý khác, nó còn mang đến sự bản lĩnh để chống lại nỗi sợ hãi, lý trí thông suốt hay sức khỏe tốt, bảo vệ bạn khỏi những năng lượng xấu và tiêu cực trong cuộc sống bộn bề.&nbsp;</p><p style="text-align: justify;">Đặc biệt, Sapphire cũng được coi là viên đá của tri thức, sự lãnh đạo sáng suốt và trung thực. Nó giúp kích thích tâm trí, khơi mở sự khôn ngoan cho những người làm công việc lãnh đạo, điều hành như các bộ trưởng, các nhà lãnh đạo, giám đốc công ty, doanh nhân…</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Thạch Anh hồng - Phù hợp mệnh Hoả và mệnh Thổ</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_hong_phan_dd657f73464348ba800ef3e0049f2b37_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Màu hồng là gam màu thuộc ngũ hành Hỏa. Đây là gam màu tượng trưng cho gam màu nóng bỏng, nổi bật, gam màu có thể mang tới cho người dùng nguồn năng lượng dồi dào, lý tưởng.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Không chỉ vậy, Thạch anh tóc hồng nổi tiếng có năng lực lớn trong chuyện tình duyên. Nó có thể phát ra năng lượng tích cực, cho cảm giác êm dịu khi yêu và trong hôn nhân. Các đôi yêu nhau hoặc vợ chồng thường đeo cặp để tránh các mối bất hòa có thể xảy ra, giúp duy trì hạnh phúc, gắn kết cho tình yêu thêm bền chặt.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Cẩm thạch tím - Phù hợp với Mệnh Hoả và Thổ</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_tim_c71db198c65e438ea224294015b41cc0_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Cẩm thạch tím&nbsp;là loại đá quý rất được tôn sùng và ngợi ca từ thời xa xưa, được coi là biểu tượng cho tâm trí sáng suốt, điềm tĩnh, có khả bảo vệ chủ nhân khỏi những tác động xấu của môi trường, mang đến cảm giác bình yên, an tâm.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Ngoài ra, đá Cẩm thạch tím sẽ giúp tâm trí được hài hòa, cân bằng, từ đó dẫn đến mọi điều trong cuộc sống trở nên suôn sẻ, những mối quan hệ được hàn gắn, những tổn thương được chữa lành hiệu quả.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Thạch Anh đỏ cánh sen - Phù hợp mệnh Hoả và Thổ</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_tim_sen_1_8dfa5123e4ae42aeab0e180de6bbed46_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Thạch anh đen sợi tóc - Phù hợp mệnh Thuỷ và mệnh Mộc</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_den_toc_1_08fdedc4c349434d98535a8c612fe900_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Thạch anh tóc đen có tác dụng cực tốt đối với người đeo về mặt phong thủy như có thể giúp mang đến sự tự tin cho người đeo, mang đến cảm giác như được bảo vệ, che chở.</p><p style="text-align: justify;">Hơn thế, có vòng thạch anh tóc đen hay các sản phẩm về thạch anh tóc đen sẽ giúp chủ nhân kìm hãm và trấn tà một cách cực kỳ hiệu quả. Từ đó mà tăng cảm giác an toàn, tránh lo âu, muộn phiền và stress trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Aquamarine Greenish blue - Phù hợp mệnh Thuỷ và Mộc</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_xanh_nhat_f6dd68df374140b2b113b5a74274372f_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Đá Aquamarine luôn được các bậc thầy phong thủy lựa chọn bởi năng lượng dương của viên đá mang tính ôn hòa, giúp người ta tìm được trạng thái tinh thần tĩnh tại. Đá Aquamarine đặc biệt được sử dụng để thỏa hiệp và đàm phán, mang viên đá này bên mình thì luôn giữ được sự bình tĩnh trong các cuộc đối thoại, xoa dịu những tranh cãi.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Không những khoác lên mình vẻ đẹp tuyệt đỉnh, chúng còn rất hữu ích trong cuộc sống. Đá phong thủy Aquamarine – trợ thủ đắc lực trong việc chữa khỏi các bệnh đường hô hấp, xoang, viêm họng, viêm phế quản. Ngoài ra công dụng đá Aquamarine giúp cân bằng lượng nước trong cơ thể, tránh trường hợp thiếu hụt nước.</p><p>&nbsp;</p><p style="text-align: justify;"><strong>Thạch anh trắng - Phù hợp mệnh Kim và mệnh Thuỷ</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/nbm_trang_1_a14259700d54451a9a092eccd5a1b367_grande.jpg"></p><p style="text-align: justify;"><strong>VỀ TRẦM HƯƠNG VIỆT NAM</strong></p><p style="text-align: justify;">Gỗ trầm hương Việt Nam được giới chuyên gia về trầm đánh giá rất cao trên thế giới về mùi hương, cũng như chất lượng nhờ vào điều kiện khí hậu &amp; thổ nhưỡng đặc trưng của rừng rậm nhiệt đới, nơi mà cây trầm phát triển và tạo trầm tốt nhất. Trầm Hương Việt Nam thường sinh trưởng tại các khu rừng rậm nhiệt đới ở Quảng Nam, Tây Nguyên, Phú Quốc,.. nơi cho ra trầm tốt nhất thế giới.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>GIỚI THIỆU VỀ&nbsp; TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: justify;">Đối với bạn trầm hương là sự may mắn, đối với Sơn Hồng, trầm hương là tâm huyết của gia đình chúng tôi. Với hơn 30 năm làm trầm, chúng tôi hiểu giá trị của Trầm Hương và mong muốn mang trầm hương Việt đến với người Việt cùng với lời chúc về sự thịnh vượng và may mắn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>CHÚNG TÔI CAM KẾT</strong></p><p style="text-align: justify;">- Nguồn gốc đúng: Trầm Hương Việt Nam được giới chuyên về trầm đánh giá tốt nhất thế giới về mùi thơm cũng như chất lượng.</p><p style="text-align: justify;">- Đúng &amp; đủ tuổi trầm như đã công bố: Chúng tôi cam kết sử dụng đúng loại trầm có số tuổi như đã công bố trên từng sản phẩm.&nbsp;</p><p style="text-align: justify;">- 100% sử dụng nguyên liệu tự nhiên: Chúng tôi không sử dụng bất kỳ hoá chất tạo màu hoặc tạo mùi nào trong tất cả các sản phẩm về trầm hương của Sơn Hồng.</p><p style="text-align: justify;"><br><strong>QUY TRÌNH SẢN XUẤT VÒNG TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/qtct-5_b4fc5c3526894490bde30264cf1ee3ae_1024x1024.jpg"></p><p>&nbsp;</p>',
