-- Product: LƯ ĐỒNG XÔNG TRẦM XÍCH ĐU
INSERT INTO products (
  haravan_product_id, name_vi, slug, description_vi, excerpt_vi,
  seo_title_vi, seo_description_vi, brand, product_type, tags, status,
  haravan_created_at, haravan_updated_at
) VALUES (
  '1043877917',
  'LƯ ĐỒNG XÔNG TRẦM XÍCH ĐU',
  'lu-dong-xong-tram-xich-du',
  '<h1 style="text-align: center;"><span style="color:#f39c12"><strong>LƯ ĐỒNG XÔNG TRẦM XÍCH ĐU</strong></span></h1><p style="text-align: justify;"><strong>THÔNG TIN CHI TIẾT</strong></p><table border="1" cellspacing="1" cellpadding="1" style="width:592px"><tbody><tr><td style="width:171px">CHẤT LIỆU</td><td style="width:407px">Đồng</td></tr><tr><td style="width:171px">KHỐI LƯỢNG</td><td style="width:407px">1000g</td></tr><tr><td style="width:171px">LOẠI TRẦM SỬ DỤNG</td><td style="width:407px">Nhang nụ, nhang vòng</td></tr><tr><td style="width:171px">BẢO HÀNH</td><td style="width:407px">1 Đổi 1 trong vòng 1 tháng nếu có lỗi kỹ thuật</td></tr><tr><td style="width:171px">SỬ DỤNG</td><td style="width:407px">Phù hợp với những người yêu thích phong thuỷ, thờ cúng, thưởng trà hay thiền tịnh tâm.</td></tr></tbody></table><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>MÔ TẢ SẢN PHẨM LƯ ĐỒNG XÔNG TRẦM XÍCH ĐU&nbsp;</strong></span></h2><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/lu_con_lac_3_cd7d9cdb8429410fbc2a66cdc704d0e9_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Lư đồng xông trầm xích đu có thiết kế tinh tế và vô cùng sang trọng. Lư xông trầm được móc trên hai chiếc dây xích, đung đưa tỏa hương trầm thơm thanh mát, giúp mang lại may mắn tài lộc cho gia chủ.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Phần đế đỡ của lư đồng được điểm thêm hình 2 rồng ngậm viên minh châu đối đầu nhau hay còn gọi là “lưỡng rồng tranh châu” có tính phong thuỷ cực cao.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Lư đồng xông trầm Sơn Hồng rất phù hợp với những người yêu thích phong thuỷ, thờ cúng, thưởng trà hay thiền tịnh tâm.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><strong><span style="color:#f39c12">CÔNG DỤNG CỦA LƯ ĐỒNG XÔNG TRẦM</span></strong></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong><em>Lư đồng xông trầm hương xích đu kiểu dáng đẹp, sang trọng, tinh tế rất thích hợp để:</em></strong></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">- Dùng để đốt trầm, đốt nhang vòng,... thư giãn tinh thần.</p><p style="text-align: justify;">- Dùng để trang trí nhà cửa, nơi làm việc hoặc thờ cúng.</p><p style="text-align: justify;">- Dùng làm quà tặng ý nghĩa cho người thân hay bạn bè.</p><p style="text-align: justify;">- Tạo sự phong thuỷ cho nơi làm việc và mang đến may mắn cho người sử dụng.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>CÁCH DÙNG CỦA LƯ ĐỒNG XÔNG TRẦM XÍCH ĐU</strong></span></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Bước 1:</strong> Mở nắp của trái châu trên xích đu và để 1 nụ trầm hoặc nhang vòng có kích thước vừa vặn vào trong.&nbsp;</p><p style="text-align: justify;"><strong>Bước 2:</strong> Châm lửa đốt phần chóp của nhang nụ trầm hương, nhang vòng/khoanh trầm hương. Khi nhang trầm hương đã cháy thổi tắt lửa và đặt vào lư và đậy nắp lư lại. Vậy bắt đầu thưởng thức hương thơm của nhang trầm hương.</p><p style="text-align: justify;"><strong>Bước 3: </strong>Có thể đung đưa nhẹ trái châu để hương thơm của nhang trầm tỏa ra được xa hơn</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>GIỚI THIỆU VỀ&nbsp; TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: justify;">Đối với bạn trầm hương là sự may mắn, đối với Sơn Hồng, trầm hương là tâm huyết của gia đình chúng tôi. Với hơn 30 năm làm trầm, chúng tôi hiểu giá trị của Trầm Hương và mong muốn mang trầm hương Việt đến với người Việt cùng với lời chúc về sự thịnh vượng và may mắn trong cuộc sống.</p><p style="text-align: justify;"><br>&nbsp;</p>',
  NULL,
  'LƯ ĐỒNG XÔNG TRẦM XÍCH ĐU',
  'Lư đồng xông trầm xích đu có thiết kế tinh tế và vô cùng sang trọng. Lư xông trầm được móc trên hai chiếc dây xích, đung đưa tỏa hương trầm thơm thanh mát, giúp mang lại may mắn tài lộc cho gia chủ.',
  'TRẦM HƯƠNG SƠN HỒNG',
  'Phụ kiện đốt trầm',
  ARRAY['Lư đồng'],
  'published',
  '2022-12-20T23:02:49+07:00'::timestamp,
