-- Product: VÒNG TRẦM HƯƠNG LÝ NGƯ BẠC CAO CẤP
INSERT INTO products (
  haravan_product_id, name_vi, slug, description_vi, excerpt_vi,
  seo_title_vi, seo_description_vi, brand, product_type, tags, status,
  haravan_created_at, haravan_updated_at
) VALUES (
  '1043918542',
  'VÒNG TRẦM HƯƠNG LÝ NGƯ BẠC CAO CẤP',
  'vong-tram-huong-ly-ngu-bac-cao-cap',
  '<h1 style="text-align: center;"><strong><span style="color:#f39c12">VÒNG TRẦM HƯƠNG LÝ NGƯ BẠC CAO CẤP</span></strong></h1><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>THÔNG TIN CHI TIẾT</strong></p><table border="1" cellspacing="1" cellpadding="1" style="width:600px"><tbody><tr><td style="width:164px">SỐ HẠT</td><td style="width:422px">17 hạt</td></tr><tr><td style="width:164px">KÍCH THƯỚC</td><td style="width:422px">&nbsp;</td></tr><tr><td style="width:164px">LOẠI TRẦM</td><td style="width:422px">Trầm hương tự nhiên cao cấp</td></tr><tr><td style="width:164px">ĐẶC TÍNH</td><td style="width:422px">Mùi thơm nhẹ nhàng, ngọt dịu</td></tr><tr><td style="width:164px">LOẠI CHARM</td><td style="width:422px">Charm bạc hình cá chép</td></tr><tr><td style="width:164px">BỘ SẢN PHẨM GỒM</td><td style="width:422px">- Vòng Trầm hương tốc cao cấp mix charm bạc hình cá chép<br>- 01 Hộp gấm bảo quản vòng<br>- 01 Thẻ Bảo Hành</td></tr><tr><td style="width:164px">BẢO HÀNH</td><td style="width:422px">1 ĐỔI 1 trong vòng 1 THÁNG nếu có lỗi kỹ thuật</td></tr><tr><td style="width:164px">SỬ DỤNG</td><td style="width:422px">Quà tặng bạn bè, người thân mang lại may mắn, bình an, tài lộc...</td></tr><tr><td style="width:164px">CÁCH BẢO QUẢN</td><td style="width:422px">Nên để vòng vào lọ thuỷ tinh (tốt nhất là có sẵn một ít bột trầm để hút ẩm và giữ hương thơm bền). Sau khi đeo một thời gian dây sẽ giảm mùi thơm, khi bỏ lại vào hũ thuỷ tinh để dưỡng hương thì vòng sẽ thơm lại một cách tự nhiên</td></tr></tbody></table><h2 style="text-align: justify;">&nbsp;</h2><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>MÔ TẢ SẢN PHẨM VÒNG TRẦM HƯƠNG LÝ NGƯ CAO CẤP</strong></span></h2><div>&nbsp;</div><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/toccc_11_1cc6bafc33f14e49a94bbc07e2ca4d9c_grande.jpg"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Vòng Trầm hương tốc Lý Ngư bạc </strong>của Sơn Hồng là chiếc vòng biểu tượng của sự thịnh vượng, sung túc và bình an.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Lượng tinh dầu của vòng trầm hương cao cấp nhiều dẫn đến trọng lượng trầm nặng khiến chúng chìm trong nước – Mùi hương khác biệt không trùng lặp. Đường vân rất đẹp và không trùng lặp, các đường nét rõ ràng.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Chiếc vòng là sự kết hợp hoàn hảo của gỗ Trầm hương tự nhiên 100 năm tuổi cùng charm bạc Lý Ngư (cá chép). Vòng tay thường được xâu chuỗi từ 17-21 hạt trầm. Số hạt sẽ được chọn dựa trên quy tắc của vòng luân hồi “sinh – lão – bệnh – tử” nhằm đem lại may mắn cho người đeo. Nên chọn số hạt là số chia cho 4 dư 1. Theo đó, hạt cuối cùng sẽ rơi vào chữ “sinh” - mang ý nghĩa may mắn và bình an.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng tay Trầm hương Sơn Hồng sở hữu mùi hương ngọt dịu, nhẹ nhàng giúp người đeo xua tan cảm giác mệt mỏi, u sầu, giải tỏa stress cũng như xoa dịu những nỗi buồn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>Ý NGHĨA CỦA BIỂU TƯỢNG LÝ NGƯ - CÁ CHÉP</strong></span></h2><div>&nbsp;</div><p style="text-align: justify;">- Biểu tượng của sự may mắn trong học hành và thi cử: Hình ảnh con cá chép khi cố gắng vượt qua cửa Vũ Môn để có thể tiến hóa thành rồng cũng sẽ tương ứng với việc học sinh viên vượt qua những những kỳ thi quan trọng khẳng định năng lực cũng như là để thành công và phát triển.</p><p style="text-align: justify;">- Biểu tượng của tiền tài và danh vọng: Cá chép là loài cá nhảy rất giỏi có thể nhảy qua cửa long môn để hóa thành rồng thì cũng sẽ là tượng trưng cho sự thăng quan, tiến chức trong công việc.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">- Biểu tượng cầu con đàn cháu đống: Cá chép là loài cá đẻ một lúc rất nhiều trứng, cũng đồng nghĩa với việc sinh con đàn, cháu đóng. Nên người dân cũng dùng hình tượng của cá chép để cầu được quý tử.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>CÔNG DỤNG CỦA VÒNG TRẦM HƯƠNG LÝ NGƯ BẠC</strong></span></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Chiếc vòng trầm hương Việt Nam tự nhiên với mặt cá chép mang lại may mắn, thịnh vượng và công việc vững vàng, gia đình thuận hòa. Lý ngư (cá chép) bằng bạc còn tăng thêm phúc khí và bảo vệ người sử dụng khỏi gió độc.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>VỀ TRẦM HƯƠNG VIỆT NAM</strong></p><p style="text-align: justify;">Gỗ trầm hương Việt Nam được giới chuyên gia về trầm đánh giá rất cao trên thế giới về mùi hương, cũng như chất lượng nhờ vào điều kiện khí hậu &amp; thổ nhưỡng đặc trưng của rừng rậm nhiệt đới, nơi mà cây trầm phát triển và tạo trầm tốt nhất. Trầm Hương Việt Nam thường sinh trưởng tại các khu rừng rậm nhiệt đới ở Quảng Nam, Tây Nguyên, Phú Quốc,.. nơi cho ra trầm tốt nhất thế giới.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>GIỚI THIỆU VỀ&nbsp; TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: justify;">Đối với bạn trầm hương là sự may mắn, đối với Sơn Hồng, trầm hương là tâm huyết của gia đình chúng tôi. Với hơn 30 năm làm trầm, chúng tôi hiểu giá trị của Trầm Hương và mong muốn mang trầm hương Việt đến với người Việt cùng với lời chúc về sự thịnh vượng và may mắn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>CHÚNG TÔI CAM KẾT</strong></p><p style="text-align: justify;">- Nguồn gốc đúng: Trầm Hương Việt Nam được giới chuyên về trầm đánh giá tốt nhất thế giới về mùi thơm cũng như chất lượng.</p><p style="text-align: justify;">- Đúng &amp; đủ tuổi trầm như đã công bố: Chúng tôi cam kết sử dụng đúng loại trầm có số tuổi như đã công bố trên từng sản phẩm.&nbsp;</p><p style="text-align: justify;">- 100% sử dụng nguyên liệu tự nhiên: Chúng tôi không sử dụng bất kỳ hoá chất tạo màu hoặc tạo mùi nào trong tất cả các sản phẩm về trầm hương của Sơn Hồng.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>QUY TRÌNH SẢN XUẤT VÒNG TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/qtct-5_b4fc5c3526894490bde30264cf1ee3ae_1024x1024.jpg"></p><p>&nbsp;</p>',
  NULL,
