INSERT INTO products (
  haravan_product_id, name_vi, slug, description_vi, excerpt_vi,
  seo_title_vi, seo_description_vi, brand, product_type, tags, status,
  haravan_created_at, haravan_updated_at
) VALUES (
  '1043921966',
  'VÒNG TỐC TRẦM HƯƠNG CHÚ ĐẠI BI - 50 NĂM TUỔI - 8Li',
  'nhan-ban-tu-vong-tram-huong-108-hat-chu-dai-bi-50-nam-tuoi-7li',
  '<p style="text-align: justify;"><strong>THÔNG TIN CHI TIẾT</strong></p><table border="1" cellspacing="1" cellpadding="1" style="width:533px"><tbody><tr><td>SỐ HẠT</td><td>17-21 hạt</td></tr><tr><td>KÍCH THƯỚC</td><td>8mm</td></tr><tr><td>LOẠI TRẦM</td><td>Trầm hương rừng tự nhiên 50 năm tuổi</td></tr><tr><td>LOẠI CHARM</td><td>Charm ngọc khắc Chú Đại Bi</td></tr><tr><td>MÀU SẮC</td><td>Đỏ, Xanh, Vàng, Trắng, Đen</td></tr><tr><td>BỘ SẢN PHẨM GỒM</td><td>-&nbsp; Vòng Trầm hương 108 hạt mix ngọc Chú Đại Bi<br>- 01 Hộp gấm bảo quản vòng<br>- 01 Thẻ Bảo Hành</td></tr><tr><td>BẢO HÀNH</td><td>1 ĐỔI 1 trong vòng 1 THÁNG nếu có lỗi kỹ thuật</td></tr><tr><td>CÁCH ĐEO THEO MỆNH</td><td><strong>- Mệnh Kim:&nbsp;</strong>Đeo<strong>&nbsp;</strong>charm ngọc<strong>&nbsp;</strong>màu<strong>&nbsp;</strong>trắng hoặc màu vàng<br><strong>- Mệnh Mộc:</strong>&nbsp;Đeo charm ngọc<strong>&nbsp;</strong>&nbsp;màu xanh hoặc màu đen<br><strong>- Mệnh Thuỷ:&nbsp;</strong>Đeo charm ngọc&nbsp;đen hoặc màu trắng<br><strong>- Mệnh Hoả:&nbsp;</strong>Đeo charm ngọc<strong>&nbsp;</strong>&nbsp;đỏ hoặc màu xanh<br><strong>- Mệnh Thổ:&nbsp;</strong>Đeo&nbsp;charm ngọc<strong>&nbsp;</strong>vàng hoặc đỏ</td></tr></tbody></table><h2 style="text-align: justify;"><strong><span style="color:#f39c12">MÔ TẢ SẢN PHẨM VÒNG TRẦM HƯƠNG CHÚ ĐẠI BI 8LI</span></strong></h2><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/43_16cec9e2a58e4b008a904f049e720c2b_grande.jpg"></p><p style="text-align: justify;">Vòng trầm hương Chú Đại Bi của Sơn Hồng là chiếc vòng tay mang màu sắc tâm linh sâu sắc. Vòng tay không chỉ là một món đồ trang sức thông thường mà trên charm ngọc mix trên chiếc vòng tay này là những dòng chữ mang công năng vi diệu của sự giải thoát, cứu khổ cứu nạn mà Quan Thế Âm Bồ Tát đã phát nguyện cứu độ chúng sanh. Vòng tay trầm hương Đại Bi chính là một lời nhắc nhở thâm thuý, mang đậm lòng từ bi đối với mỗi chúng ta trên con đường tu tập, giác ngộ Phật tánh.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng được chế tác từ gỗ Trầm hương tự nhiên Việt Nam 50 năm tuổi 7mm kết hợp cùng charm ngọc khắc Chú Đại Bi với giúp người đeo tăng cường năng lượng tích cực, thu hút tài lộc và bình an trong cuộc sống</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Vòng tay Trầm hương mix ngọc Chú Đại Bi sở hữu mùi hương ngọt dịu, nhẹ nhàng giúp người đeo xua tan cảm giác mệt mỏi, u sầu, giải tỏa stress cũng như xoa dịu những nỗi buồn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><strong><span style="color:#f39c12">Ý NGHĨA CỦA BIỂU TƯỢNG CỦA CHÚ ĐẠI BI</span></strong></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Chú Đại Bi là câu thần chú được trích từ Kinh Thiên Thủ Thiên Nhãn Quán Thế Âm Bồ Tát Quảng Đại Viên Mãn Vô Ngại Đại Bi Tâm Đà Ra Ni. Và Chú Đại Bi là câu thần chú mang uy lực có tác động mạnh mẽ đến tất thảy chúng sanh trên con đường giải thoát.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Câu thần chú mong muốn chúng sanh được an vui, diệt trừ được tất thảy bệnh tật, sống lâu, được giàu có, được tiêu trừ toàn bộ nghiệp chướng của nhiều đời nhiều kiếp, được chứng quả thành tựu công đức, được thành tựu tất cả thiện căn, không lo sợ hãi, được mong cầu đầy đủ như ý.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>VỀ TRẦM HƯƠNG VIỆT NAM</strong></p><p style="text-align: justify;">Gỗ trầm hương Việt Nam được giới chuyên gia về trầm đánh giá rất cao trên thế giới về mùi hương, cũng như chất lượng nhờ vào điều kiện khí hậu &amp; thổ nhưỡng đặc trưng của rừng rậm nhiệt đới, nơi mà cây trầm phát triển và tạo trầm tốt nhất. Trầm Hương Việt Nam thường sinh trưởng tại các khu rừng rậm nhiệt đới ở Quảng Nam, Tây Nguyên, Phú Quốc,.. nơi cho ra trầm tốt nhất thế giới.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>GIỚI THIỆU VỀ&nbsp; TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: justify;">Đối với bạn trầm hương là sự may mắn, đối với Sơn Hồng, trầm hương là tâm huyết của gia đình chúng tôi. Với hơn 30 năm làm trầm, chúng tôi hiểu giá trị của Trầm Hương và mong muốn mang trầm hương Việt đến với người Việt cùng với lời chúc về sự thịnh vượng và may mắn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>CHÚNG TÔI CAM KẾT</strong></p><p style="text-align: justify;">- Nguồn gốc đúng: Trầm Hương Việt Nam được giới chuyên về trầm đánh giá tốt nhất thế giới về mùi thơm cũng như chất lượng.</p><p style="text-align: justify;">- Đúng &amp; đủ tuổi trầm như đã công bố: Chúng tôi cam kết sử dụng đúng loại trầm có số tuổi như đã công bố trên từng sản phẩm.&nbsp;</p><p style="text-align: justify;">- 100% sử dụng nguyên liệu tự nhiên: Chúng tôi không sử dụng bất kỳ hoá chất tạo màu hoặc tạo mùi nào trong tất cả các sản phẩm về trầm hương của Sơn Hồng.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>QUY TRÌNH SẢN XUẤT VÒNG TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/qtct-5_b4fc5c3526894490bde30264cf1ee3ae_1024x1024.jpg"></p><p>&nbsp;</p>',
  NULL,
  'VÒNG TỐC TRẦM HƯƠNG CHÚ ĐẠI BI - 50 NĂM TUỔI - 8Li',
  'Vòng được chế tác từ gỗ Trầm hương tự nhiên Việt Nam 50 năm tuổi 7mm kết hợp cùng charm ngọc khắc Chú Đại Bi với giúp người đeo tăng cường năng lượng tích cực, thu hút tài lộc và bình an. 
