) VALUES (
  '1044172478',
  'TƯỢNG TRẦM HƯƠNG TỨ LINH',
  'tuong-tram-huong-tu-linh',
  '<h1 style="text-align: center;"><strong><span style="color:#f39c12">TƯỢNG TRẦM HƯƠNG TỨ LINH</span></strong></h1><div style="text-align: justify;">&nbsp;</div><div><p style="text-align: justify;"><strong>THÔNG TIN SẢN PHẨM</strong></p><table border="1" cellspacing="1" cellpadding="1" style="width:600px"><tbody><tr><td style="width:166px">KÍCH THƯỚC</td><td style="width:420px">&nbsp;</td></tr><tr><td style="width:166px">TRỌNG LƯỢNG</td><td style="width:420px">&nbsp;</td></tr><tr><td style="width:166px">LOẠI TRẦM</td><td style="width:420px">Trầm hương tự nhiên</td></tr><tr><td style="width:166px">ĐẶC TÍNH</td><td style="width:420px">Mùi hương dịu nhẹ, thanh khiết</td></tr><tr><td style="width:166px">BẢO HÀNH</td><td style="width:420px">Theo nhà sản xuất</td></tr></tbody></table><h2 style="text-align: justify;">&nbsp;</h2><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>MÔ TẢ SẢN PHẨM TƯỢNG TRẦM HƯƠNG TỨ LINH</strong></span></h2><div>&nbsp;</div><p style="text-align: center"><img src="//file.hstatic.net/200000591825/file/6_ce2385f1ca024f0a8e8c41917b8a91e1_grande.png"></p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>Tượng Trầm Hương Tứ Linh</strong> của Trầm Hương Sơn Hồng là sự kết hợp 4 linh vật đất trời, mang ý nghĩa là tứ trụ cho gia chủ, giúp hỗ trợ tăng cường công danh, tài lộc, quyền lực, sự trường thọ, cao quý, hạnh phúc, may mắn,...&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Từ 100% gỗ trầm hương cao cấp, những nghệ nhân lành nghề đã điêu khắc lên một bức tượng Trầm Hương vô cùng ý nghĩa và độc nhất.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Tứ linh - long lân quy phụng là những con vật được tạo ra từ bốn phương trời, quan niệm người xưa cho rằng chúng đại diện cho 4 nguyên tố chính của trời đất: nước, lửa, đất và gió.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><h2 style="text-align: justify;"><span style="color:#f39c12"><strong>VỊ TRÍ ĐẶT TRẦM CẢNH ĐỂ HỢP PHONG THUỶ</strong></span></h2><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Đặt Tượng Trầm Hương Tứ Linh có tính phong thuỷ và ý nghĩa tâm linh cao, chính vì vậy mà chúng ta nên đặt tượng ở nơi cao ráo, trang nghiêm trong ngôi nhà của mình.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>VỀ TRẦM HƯƠNG VIỆT NAM</strong></p><p style="text-align: justify;">Gỗ trầm hương Việt Nam được giới chuyên gia về trầm đánh giá rất cao trên thế giới về mùi hương, cũng như chất lượng nhờ vào điều kiện khí hậu &amp; thổ nhưỡng đặc trưng của rừng rậm nhiệt đới, nơi mà cây trầm phát triển và tạo trầm tốt nhất. Trầm Hương Việt Nam thường sinh trưởng tại các khu rừng rậm nhiệt đới ở Quảng Nam, Tây Nguyên, Phú Quốc,.. nơi cho ra trầm tốt nhất thế giới.&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>GIỚI THIỆU VỀ&nbsp; TRẦM HƯƠNG SƠN HỒNG</strong></p><p style="text-align: justify;">Đối với bạn trầm hương là sự may mắn, đối với Sơn Hồng, trầm hương là tâm huyết của gia đình chúng tôi. Với hơn 30 năm làm trầm, chúng tôi hiểu giá trị của Trầm Hương và mong muốn mang trầm hương Việt đến với người Việt cùng với lời chúc về sự thịnh vượng và may mắn trong cuộc sống.</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;"><strong>CHÚNG TÔI CAM KẾT</strong></p><p style="text-align: justify;">- Nguồn gốc đúng: Trầm Hương Việt Nam được giới chuyên về trầm đánh giá tốt nhất thế giới về mùi thơm cũng như chất lượng.</p><p style="text-align: justify;">- Đúng &amp; đủ tuổi trầm như đã công bố: Chúng tôi cam kết sử dụng đúng loại trầm có số tuổi như đã công bố trên từng sản phẩm.&nbsp;</p><p style="text-align: justify;">- 100% sử dụng nguyên liệu tự nhiên: Chúng tôi không sử dụng bất kỳ hoá chất tạo màu hoặc tạo mùi nào trong tất cả các sản phẩm về trầm hương của Sơn Hồng.<br><br>&nbsp;</p></div>',
